INSERT INTO news (title, content, category, credibility_rating, source, is_premium, image_url) VALUES
('Анонсирован новый фильм про Мстителей на 2025 год', 'Marvel Studios официально подтвердила выход нового блокбастера про команду Мстителей. Премьера запланирована на лето 2025 года. В главных ролях вернутся культовые персонажи.', 'Премьеры', 5, 'Marvel Studios Official', false, 'https://cdn.poehali.dev/projects/20408bd5-86c4-408f-a88f-6646c3a6d8f0/files/a7af761c-633d-4414-a3cd-bd3497370f1d.jpg'),
('Раскрыты детали 6-й фазы MCU: что нас ждёт?', 'Кевин Файги поделился планами Marvel на ближайшие годы. 6-я фаза обещает стать самой масштабной в истории киновселенной с новыми персонажами и неожиданными кроссоверами.', 'Новости', 5, 'Kevin Feige Interview', false, 'https://cdn.poehali.dev/projects/20408bd5-86c4-408f-a88f-6646c3a6d8f0/files/bc9f855a-3263-4686-9fcb-ba67f168f938.jpg'),
('Эксклюзивное интервью с режиссёром Дэдпул и Росомаха', 'Шон Леви рассказал о съёмках, импровизациях Райана Рейнольдса и будущем франшизы. Это интервью доступно только для премиум-подписчиков!', 'Интервью', 5, 'Exclusive Interview', true, 'https://cdn.poehali.dev/projects/20408bd5-86c4-408f-a88f-6646c3a6d8f0/files/22e58ebf-a0a9-4cd6-a90a-6f8d03344b4d.jpg'),
('Слухи: Железный Человек может вернуться в MCU', 'Инсайдеры утверждают, что Тони Старк может появиться в одном из будущих фильмов. Официального подтверждения пока нет.', 'Слухи', 2, 'Industry Insider', false, 'https://cdn.poehali.dev/projects/20408bd5-86c4-408f-a88f-6646c3a6d8f0/files/bc9f855a-3263-4686-9fcb-ba67f168f938.jpg'),
('Секретные материалы: Удалённые сцены из Финала', 'Получите доступ к эксклюзивным удалённым сценам из Мстители: Финал. Материал доступен только подписчикам Premium.', 'Эксклюзив', 5, 'Marvel Archives', true, 'https://cdn.poehali.dev/projects/20408bd5-86c4-408f-a88f-6646c3a6d8f0/files/a7af761c-633d-4414-a3cd-bd3497370f1d.jpg'),
('Новый костюм Человека-паука: первые кадры', 'Фотографии со съёмочной площадки показывают новый дизайн костюма Питера Паркера. Фанаты уже активно обсуждают изменения.', 'Новости', 4, 'Set Photos', false, 'https://cdn.poehali.dev/projects/20408bd5-86c4-408f-a88f-6646c3a6d8f0/files/22e58ebf-a0a9-4cd6-a90a-6f8d03344b4d.jpg');
INSERT INTO secret_materials (title, content, material_type, image_url) VALUES
('Концепт-арты: Альтернативные костюмы Мстителей', 'Эксклюзивная подборка концепт-артов, которые не вошли в финальные версии фильмов. Увидьте, как могли бы выглядеть ваши любимые герои!', 'Концепт-арт', 'https://cdn.poehali.dev/projects/20408bd5-86c4-408f-a88f-6646c3a6d8f0/files/a7af761c-633d-4414-a3cd-bd3497370f1d.jpg'),
('За кадром: Создание спецэффектов в Войне Бесконечности', 'Уникальное видео о том, как создавались самые зрелищные сцены фильма. Интервью с командой VFX и эксклюзивные материалы.', 'Видео', 'https://cdn.poehali.dev/projects/20408bd5-86c4-408f-a88f-6646c3a6d8f0/files/bc9f855a-3263-4686-9fcb-ba67f168f938.jpg'),
('Секретный сценарий: Вырезанная сюжетная линия Таноса', 'Читайте альтернативную историю Таноса, которая была написана, но не вошла в фильм. Эксклюзив для премиум-подписчиков.', 'Документ', 'https://cdn.poehali.dev/projects/20408bd5-86c4-408f-a88f-6646c3a6d8f0/files/22e58ebf-a0a9-4cd6-a90a-6f8d03344b4d.jpg');